sys_ui_script.script:
angular.module("app_angular_menu", ['sn.glideAjax']);
angular.module("app_angular_menu").controller("MenuController", 
	function($scope, GlideAjax){
		var scope = "x_44552_menu_das";
		$scope.searchText = "";
		$scope.title = "Menu - Dashboard";
		$scope.subtitle = "Today is a good day to code";
		$scope.tiles = [];
		$scope.message = "loading data...";
		$scope.data = [];
		$scope.show = "tiles";

		$scope.clickTile = function(tile){
			$scope.loadingData = true;
			$scope.message = "loading tiles app_angular_menu " + tile.name ;
			getTiles(tile);
		};

		$scope.showTiles = function(){
			$scope.show = "tiles";
		};

		$scope.showApplications = function(){
			$scope.show = "applications";
		};

		$scope.selectApplication = function(application){
			application.checked = !application.checked;
			if(application.checked){
				$scope.data.push(application.name);
			}else{
				var index = $scope.data.indexOf(application.name);
				$scope.data.splice(index, 1);
			}

			$scope.message = $scope.data.length + " tiles selected";
		};

		$scope.saveTiles = function(){
			$scope.message = "saving...";
			var ga = new GlideAjax("MenuDashboard");
			ga.addParam("sysparm_name", "saveTiles");
			ga.addParam("sysparm_inputs", JSON.stringify($scope.data));
			ga.setScope(scope);
			ga.getJSON(function(response){
				if(!response){
					$scope.message = "No response received";
					return;
				}

				$scope.message = response.success;
			});
		};

		$scope.deleteTile = function(tile){
			if(confirm("Are you sure you want to delete " + tile.name + " ?")){
				$scope.message = "please wait...";
				var ga = new GlideAjax("MenuDashboard");
				ga.addParam("sysparm_name", "deleteTile");
				ga.addParam("sys_id", tile.sys_id);
				ga.setScope(scope);
				ga.getJSON(function(response){
					if(!response){
						$scope.message = "No response received";
						return ;
					}

					if(!response.success){
						$scope.message = response.error;
						return ;
					}


					$scope.message = response.success;
					$scope.tiles.splice(tile.sys_id, 1);
				});
			}
		};

		load();

		function load(){
			getTiles();
		}

		function getApplications(){
			var ga = new GlideAjax("MenuDashboard");
			ga.addParam("sysparm_name", "getApplications");
			ga.setScope(scope);
			ga.getJSON(function(response){
				if(!response){
					$scope.message = "No response received";
					return ;
				}

				if(response.error){
					$scope.message = response.message;
					return ;
				}

				$scope.applications = response;
			});
		}

		function getTiles(parent){
			$scope.parent = parent;

			var ga = new GlideAjax("MenuDashboard");
			ga.addParam("sysparm_name", "getTiles");
			ga.setScope(scope);
			ga.getJSON(function(response){
				if(!response){
					$scope.message = "No response received";
					return ;
				}

				if(response.error){
					$scope.message = response.message;
					return ;
				}

				$scope.message = response.length + " tiles loaded";
				$scope.tiles = response;
			});		
		}
	}
);
